import React, { Component } from 'react';
 import axios from 'axios';
 import { Table, Button } from 'react-bootstrap';
 // To use routing functionalities
 import { Link } from 'react-router-dom';

 import ScheduleService from './Services';
 
 var divStyle = {
 margin: '8% 8%',
 };
 
 class AllTimeTables extends Component {
 
 constructor(props) {
 super(props);
 this.scheduleService = new ScheduleService();
 this.state = {
 timetable: []
 }
 this.delete = this.delete.bind(this);
 }
 
 componentDidMount = () => {
 this.getTimetableList();
 }
 
 // To get all the employees
 getTimetableList() {
 axios.get('http://localhost:8070/timetable')
 .then((response) => {
 console.log(response);
 this.setState({
 timetable: response.data
 });
 })
 .catch((error) => {
 console.log(error);
 })
 }
 
 // To delete any employee
 delete(id) {
 this.scheduleService.delete(id);
 this.getTimetableList();
 }
 
 render() {
 const {timetable } = this.state;
 return (
 <div style={divStyle}>
 <Table responsive>
 <thead>
 <tr>
 <th>#</th>
 <th>Subject NAme</th>
 <th>Day</th>
 <th>TimeStart</th>
 <th>TimeEnd</th>
 <th></th>
 <th></th>
 </tr>
 </thead>
 <tbody>
 {
 timetable && timetable.map((Timetable, i) => {
 return (
 <tr key={i}>
 <td>{i}</td>
 <td>{Timetable.subjectName}</td>
 <td>{Timetable.Day}</td>
 <td>{Timetable.TimeStart}</td>
 <td>{Timetable.TimeEnd}</td>
 <td>
 <Link to="`EditTimeTable/${Timetable._id}`" className="btn btn-primary">Edit</Link>
 </td>
 <td>
 <Button onClick={() => this.delete(Timetable._id)} bsStyle="danger" >Delete</Button>
 
 </td>
 </tr>
 )
 })
 }
 </tbody>
 
 </Table>
 
 </div>
 );
 } 
 }
 
 export default AllTimeTables;
