import React, { Component } from "react";
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import TimetableCard from "./timetableCard";
import "../css/tt.css";


export default class ListTimetable extends Component {
  constructor(props) {
    super(props)
    this.state = {
      timetables: []
    };
  }
  componentDidMount() {
    axios.get('http://localhost:8070/timetables/')
      .then(res => {
        this.setState({
          timetables: res.data
        });
      })
      .catch((error) => {
        console.log(error);
      })
  }
  DataTable() {
    return this.state.timetables.map((res, i) => {
      
        return <TimetableCard obj={res} key={i} />;
      });
  }

  render() {
    return (
     
    <div className="table-wrapper">
       <h2>Timetables</h2>
      <Table striped bordered hover className="card1" >
       
       
         <tr>{this.DataTable()}</tr>
          
        
      </Table>
    </div>);
  }
}
