import React from 'react';

import { useState , useEffect} from 'react';
import axios from 'axios';


function AddTimeTable(){
    

  const[subjectName,setsubjectName] = useState("");
  const[Day,setDay] = useState("");
  const[TimeStart,setTimeStart] = useState("");
  const[TimeEnd,setTimeEnd] = useState("");

  function sendData(e){
      e.preventDefault();
     
      const newTimetable = {
          subjectName,Day,TimeStart,TimeEnd
      }
      //http request(frontend to backend)
      axios.post("http://localhost:8070/timetable/add",newTimetable).then(()=>{
      alert("Timetable Added")})
      /*.catch((err)=>{
          alert(err)
      })*/
  }
  

  return(
      <form onSubmit={sendData}>
          <div className = "container">
          <div class="mb-3">
              <label for="subject" class="form-label">Subject Name</label>
              <input onChange={(e)=>{
                  setsubjectName(e.target.value);
              }}
              type="text" class="form-control" id="subject" value={subjectName}/>
          </div>

          
          <div class="mb-3">
              <label for="day" class="form-label">Day</label>
              <input onChange={(e)=>{
                  setDay(e.target.value);
              }}
              type="text" class="form-control" id="day" />
          </div>

          <div class="mb-3">
              <label for="ts" class="form-label">Time Start</label>
              <input onChange={(e)=>{
                  setTimeStart(e.target.value);
              }} 
              type="text" class="form-control" id="ts" />
          </div>

          <div class="mb-3">
              <label for="te" class="form-label">Time End</label>
              <input onChange={(e)=>{
                  setTimeEnd(e.target.value);
              }}
              type="text" class="form-control" id="te" />
          </div>

         
          <button type="submit" class="btn btn-primary" >Submit</button><br/>
          <a href="/add">Back</a>

         <br/>
         <a href="/fetch">Timetable</a>
          </div>

          </form>
        
  )

  
}


export default AddTimeTable;
