import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import axios from 'axios';


class AddTimeTable  extends Component {
  constructor(props) {
    super(props);
    this.state = {
        subjectName:'',
        Day: '',
        TimeStart : '',
        TimeEnd : ''

    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const data = {
        subjectName:this.state.subjectName,
        Day:this.state.Day,
        TimeStart :this.state.TimeStart,
        TimeEnd : this.state.TimeEnd

    };

    axios
      .post("http://localhost:8070/timetable/add",data)
      .then(res => {
        this.setState({
            subjectName: '',
            Day : '',
            TimeStart : '',
            TimeEnd : '' 
    
        })
       
      })
      .catch(err => {
        console.log("Error in creating!");
      })
    }
 render(){
return(
  <form onSubmit={this.onSubmit}>
      <div className = "container">
          <div class="mb-3">
          <label for="subject" class="form-label">Subject Name</label>
             <input type="text" class="form-control" placeholder = 'Subject name' name='name' value={this.state.subjectName} onChange={this.onChange}/>
          </div>
          <div class="mb-3">
          <label for="subject" class="form-label">Day</label>
             <input type="text" class="form-control" placeholder = 'Day' name='day' value={this.state.Day} onChange={this.onChange}/>
          </div>
          <div class="mb-3">
          <label for="subject" class="form-label">TimeStart</label>
             <input type="text" class="form-control" placeholder = 'time start' name='start' value={this.state.TimeStart} onChange={this.onChange}/>
          </div>
          <div class="mb-3">
          <label for="subject" class="form-label">TimeEnd</label>
             <input type="text" class="form-control" placeholder = 'time end' name='end' value={this.state.TimeEnd} onChange={this.onChange}/>
          </div>
          <input
                    type="submit"
                    className="btn btn-outline-warning btn-block mt-4"
                />
      
          </div>

  </form>
);


}}

export default AddTimeTable;
