import React, { Component } from 'react';
import axios from 'axios';


export default class EditEmployee extends Component {
  constructor(props) {
    super(props);

    this.onChangefirstName = this.onChangefirstName.bind(this);
    this.onChangelastName = this.onChangelastName.bind(this);
    this.onChangeemail = this.onChangeemail.bind(this);
    this.onChangephone = this.onChangephone.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      firstName:'',
      lastName:'',
      email:'',
      phone:'',
      employees:[]
    }
  }

  componentDidMount() {
    axios.get('http://localhost:8070/employees/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email,
          phone: response.data.phone,
        })   
      })
      .catch(function (error) {
        console.log(error);
      })

    /*axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username),
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }*/

  this.onChangefirstName(e); {
    this.setState({
      firstName:e.target.value
    })
  }

  this.onChangelastName(e); {
    this.setState({
      lastName: e.target.value
    })
  }

  this.onChangeemail(e); {
    this.setState({
      email: e.target.value
    })
  }

  this.onChangephone(e) ;{
    this.setState({
      phone: e.target.value
    })
  }

  this.onSubmit(e); {
    e.preventDefault();

    const employee = {
      firstName: this.state.firstName,
      lastName:this.state.lastName,
      email:this.state.email,
      phone:this.state.phone
    }

    console.log(employee);

    axios.post('http://localhost:8070/employees/updateEmployee/' + this.props.match.params.id, employee)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render(); {
    return (
    <div>
      <h3>Edit </h3>
      <form onSubmit={this.onSubmit}>
       
        <div className="form-group"> 
          <label>First Name: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.firstName}
              onChange={this.onChangefirstName}
              />
        </div>
        <div className="form-group">
          <label>lastName </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.lastName}
              onChange={this.onChangelastName}
              />
        </div>
        <div className="form-group">
          <label>email </label>
          <div>
            <DatePicker
              selected={this.state.email}
              onChange={this.onChangeemail}
            />
          </div>
        </div>
        <div className="form-group">
          <label>phone </label>
          <div>
            <DatePicker
              selected={this.state.phone}
              onChange={this.onChangephone}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Edit Exercise Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
}
