import React, { Component } from 'react';
 import axios from 'axios';
 
 const customStyle = {
 width: '300px',
 margin: '0 auto'
 }
 
 class EditTimetable extends Component {
 constructor(props) {
 super(props);
 this.state = {
  subjectName:'',
  Day: '',
  TimeStart : '',
  TimeEnd : ''

 }
 }
 
 componentDidMount = () => {
 this.getTimetableById();
 }
 
 // To get employee based on ID
 getTimetableById() {
 axios.get('http://localhost:8070/timetable/edit/' + this.props.match.params.id)
 .then((response) => {
 this.setState({
 subjectName: response.data.subjectName,
 Day: response.data.Day,
 TimeStart: response.data.TimeStart,
 TimeEnd: response.data.TimeEnd
 });
 })
 .catch((error) => {
 console.log(error);
 })
 }
 
 handleChange = (event) => {
 this.setState({ [event.target.name]: event.target.value });
 }
 
 // To update the record on submit
 handleSubmit = (event) => {
 event.preventDefault();
 const { subjectName,Day,TimeStart,TimeEnd} = this.state;
 axios.post('http://localhost:8070/timetable/update/' + this.props.match.params.id, {
 subjectName:subjectName,
 Day:Day,
 TimeStart:TimeStart,
 TimeEnd:TimeEnd
 })
 .then((response) => {
 console.log(response);
 this.props.history.push('/');
 })
 .catch((error) => {
 console.log(error);
 });
 
 }
 
 render() {
 return (
 <div className="container">
 <form style={customStyle} onSubmit={this.handleSubmit}>
 <label>
 subject Name
 <input
 name="subject name"
 type="text"
 value={this.state.subjectName}
 onChange={this.handleChange}
 className="form-control"
 />
 </label>
 <br />
 <label>
 Day
 <input
 name="day"
 type="text"
 value={this.state.Day}
 onChange={this.handleChange}
 className="form-control"
 />
 </label>
 <br />
 <label>
 Time Start
 <input
 name="time start"
 type="text"
 value={this.state.TimeStart}
 onChange={this.handleChange}
 className="form-control"
 />
 </label>
 <br />
 <label>
 Time End
 <input
 name="time end"
 type="text"
 value={this.state.TimeEnd}
 onChange={this.handleChange}
 className="form-control"
 />
 </label>
 <br />
 <input
 type="submit"
 value="submit"
 className="btn btn-primary"
 />
 </form>
 </div>
 );
 }
 }
 
 export default EditTimetable;
