import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import axios from 'axios';


class AddTimeTable  extends Component {
  constructor() {
    super();
    this.state = {
        subjectName:'',
        Day: '',
        TimeStart : '',
        TimeEnd : ''

    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const data = {
        subjectName:this.state.subjectName,
        Day:this.state.Day,
        TimeStart :this.state.TimeStart,
        TimeEnd : this.state.TimeEnd

    };

    axios
      .post("http://localhost:8070/timetable/add",data)
      .then(res => {
        this.setState({
            subjectName: '',
            Day : '',
            TimeStart : '',
            TimeEnd : '' 
    
        })
       
      })
      .catch(err => {
        console.log("Error in creating!");
      })
  

return(
<form onSubmit={sendData}>
<div className = "container">
<div class="mb-3">
    <label for="subject" class="form-label">Subject Name</label>
    <input onChange={(e)=>{
        setsubjectName(e.target.value);
    }}
    type="text" class="form-control" id="subject" />
</div>


<div class="mb-3">
    <label for="day" class="form-label">Day</label>
    <input onChange={(e)=>{
        setDay(e.target.value);
    }}
    type="text" class="form-control" id="day" />
</div>

<div class="mb-3">
    <label for="ts" class="form-label">Time Start</label>
    <input onChange={(e)=>{
        setTimeStart(e.target.value);
    }} 
    type="text" class="form-control" id="ts" />
</div>

<div class="mb-3">
    <label for="te" class="form-label">Time End</label>
    <input onChange={(e)=>{
        setTimeEnd(e.target.value);
    }}
    type="text" class="form-control" id="te" />
</div>


<button type="submit" class="btn btn-primary" >Submit</button><br/>
<a href="/add">Back</a>

<br/>
<a href="/fetch">Timetable</a>
</div>

</form>

)




}
}
export default AddTimeTable;
