import React from "react";
import axios from "axios";
import {  Button } from 'react-bootstrap';


class ListStaff extends React.Component
{
    state = {
        employees: [],
        UserID:'',
        firstName:'',
        lastName: '',
        email:'',
        phone:'',
        password:''
       
     


    }
    getStaff = ()=>{
        axios.get("http://localhost:8070/employees/").then(res=>{
            console.log(res);
            this.setState({employees:res.data});
        })
        
        }
        componentDidMount = ()=>{
            this.getStaff();
        }
        handleDelete = (id)=>{
            axios.get(`http://localhost:8070/employees/deleteEmployee/${id}`)
            .then(res=>{
                console.log(res);
                window.location = '/getlist';
            }) 
        }
        handleUpdate = (e)=>{
            this.setState({[e.target.name]:e.target.value});

        }
        handleModalUpdate = (id)=>{
            axios.post(`http://localhost:8070/employees/updateEmployee/${id}`,
            {   firstName:this.state.firstName,
                lastName:this.state.lastName,
                email:this.state.email,
                phone:this.state.phone,


            }).then(res=>{
                console.log(res);
                this.setState({
                    firstName:'',
                    lastName: '',
                    email:'',
                    phone:''

                }) 
                window.location = '/getlist';
            })
        }
        render()
        {
            return(
                <div>
                    <h1><center class="text-primary">All Teachers.</center></h1>
                    {
                        this.state.employees.map(employees=>(
                            <div key ={employees._id} class="border border-danger" style={{borderRadius:'15px',padding:'15px',display:"inline-block",marginLeft:'15px',marginTop:'20px'}}>
                                
                                <div class="card-body">
                                    
                                    <h5>First Name: {employees.firstName}</h5>
                                    <h5>Last Name:{employees.lastName}</h5>
                                    <h5>email:{employees.email}</h5>
                                    <h5>phone:{employees.phone}</h5>

  <div class="container" style={{display:'inline'}}>
  
  <Button style={{marginLeft:'20px'}} type="button" class="btn btn-warning" data-toggle="modal" data-target="#myModal"
   onClick={()=>{this.setState({UserID:employees._id,firstName:employees.firstName,lastName:employees.lastName,email:employees.email,phone:employees.phone,password:employees.password})}}>Update</Button>
  <button style={{marginLeft:'20px'}} onClick={()=>this.handleDelete(employees._id)} class="btn btn-danger">Delete</button>
  <div class="modal fade" id="myModal" role="dialog">
    <div class="modal-dialog">  
    <div class="modal-content">
        <div class="modal-header">
          <button type="button" className="close" data-dismiss="modal">&times;</button>
          <h4 class="modal-title">UPDATE DETAILS</h4> 
        </div>
        <div class="modal-body">
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.firstName }  name='firstName' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="fName" />
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.lastName } name='lastName' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="lName"/>
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.email } name='email' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="emailr"/> 
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.phone} name='phone' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Phone Number"/>
          
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-danger" data-dismiss="modal"onClick={()=>{this.setState({firstName:'',lastName:'',email:'',phone:''})}}>Close</button> 
          <button type="button" class="btn btn-primary" onClick={(e)=>this.handleModalUpdate(e)}>Save Changes</button>
        </div>
      </div>
      
    </div>
  </div>
  
</div>
</div>
</div>

 ))
    }
                    

                </div>
            );
        }
        

    }
 
export default ListStaff;
