import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';
export default class AddNotice extends Component {
  constructor(props) {
    super(props)
    // Setting up functions
    this.onChangeNotice = this.onChangeNotice.bind(this);
    this.onChangeDay = this.onChangeDay.bind(this);
    
    this.onSubmit = this.onSubmit.bind(this);
    // Setting up state
    this.state = {
      Notice:'',
      Day:''
    }
  }
  onChangeNotice(e) {
    this.setState({ Notice: e.target.value })
  }
  onChangeDay(e) {
    this.setState({ Day: e.target.value })
  }
  
  onSubmit(e) {
    e.preventDefault()
    const noticetObject = {
      Notice: this.state.Notice,
      Day: this.state.Day,
      
    };
    axios.post('http://localhost:8070/notices/addNotice', noticeObject)
      .then(res => console.log(res.data));
    this.setState({ Notice:'', Day:''})
  }
  render() {
    return (<div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
       
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" value={this.state.name} onChange={this.onChangeStudentName} />
        
       
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" value={this.state.email} onChange={this.onChangeStudentEmail} />
        
       
        <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
          Create Student
        </Button>
      </Form>
    </div>);
  }
}
