import React from "react";
import axios from "axios";


class ListTeacher extends React.Component
{
    state = {
        teachers: [],
        uname: '',
        uage:'',
        ugender:'',
        uphoneNumber:'',
        uemail:'',
        udescription:'',
        uid:''
       
     


    }
    getTeachers = ()=>{
        axios.get("http://localhost:8070/teacher/").then(res=>{
            console.log(res);
            this.setState({teachers:res.data});
        })
        
        }
        componentDidMount = ()=>{
            this.getTeachers();
        }
        handleDelete = (id)=>{
            axios.delete(`http://localhost:8070/teacher/delete/${id}`)
            .then(res=>{
                console.log(res);
                window.location = '/';
            }) 
        }
        handleUpdate = (e)=>{
            this.setState({[e.target.name]:e.target.value});

        }
        handleModalUpdate = (e)=>{
            axios.put(`http://localhost:8070/teacher/update/${this.state.uid}`,{name:this.state.uname,age:this.state.uage,gender:this.state.ugender,phoneNumber:this.state.uphoneNumber,email:this.state.uemail,description:this.state.udescription}).then(res=>{
                console.log(res);
                this.setState({uname:'',uage:'',ugender:'',uphoneNumber:'',uemail:'',udescription:''}) 
                window.location = '/';
            })
        }
        render()
        {
            return(
                <div>
                    <h1><center class="text-primary">All Teachers.</center></h1>
                    {
                        this.state.teachers.map(teachers=>(
                            <div key ={teachers._id} class="border border-danger" style={{borderRadius:'15px',padding:'15px',display:"inline-block",marginLeft:'15px',marginTop:'20px'}}>
                                
                                <div class="card-body">
                                    
                                    <h5>Name: {teachers.name}</h5>
                                    <h5>Age: {teachers.age}</h5>
                                    <h5>Gender: {teachers.gender}</h5>
                                    <h5>Phone Number: {teachers.phoneNumber}</h5>
                                    <h5>E-mail: {teachers.email}</h5>
                                    <h5>Description: {teachers.description}</h5>

  <div class="container" style={{display:'inline'}}>
  <a href="#" button class="btn btn-info">View Profile </a>
  <Button style={{marginLeft:'20px'}} type="button" class="btn btn-warning" data-toggle="modal" data-target="#myModal" onClick={()=>{this.setState({uid:teachers._id,uname:teachers.name,uage:teachers.age,ugender:teachers.gender,uphoneNumber:teachers.phoneNumber,uemail:teachers.email,udescription:teachers.description})}}>Update</Button>
  <Button style={{marginLeft:'20px'}} onClick={()=>this.handleDelete(teachers._id)} class="btn btn-danger">Delete</Button>
  <div class="modal fade" id="myModal" role="dialog">
    <div class="modal-dialog">  
    <div class="modal-content">
        <div class="modal-header">
          <button type="button" class="close" data-dismiss="modal">&times;</button>
          <h4 class="modal-title">UPDATE DETAILS</h4> 
        </div>
        <div class="modal-body">
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.uname }  name='uname' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Name" />
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.uage } name='uage' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Age"/>
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.ugender } name='ugender' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Gender"/> 
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.uphoneNumber} name='uphoneNumber' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Phone Number"/>
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.uemail } name='uemail' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="E-mail"/>
          <input  onChange={(e)=>this.handleUpdate(e)} value={this.state.udescription } name='udescription' class="from-control" style={{marginBottom:'10px',fontFamily:'cursive',fontSize: '25x'}} placeholder="Description"/>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-danger" data-dismiss="modal"onClick={()=>{this.setState({uname:'',uage:'',ugender:'',uphoneNumber:'',uemail:'',udescription:''})}}>Close</button> 
          <button type="button" class="btn btn-primary" onClick={(e)=>this.handleModalUpdate(e)}>Save Changes</button>
        </div>
      </div>
      
    </div>
  </div>
  
</div>
</div>
</div>

 ))
    }
                    

                </div>
            );
        }
        

    }
 
export default ListTeacher;
