import React , { Component} from 'react';
import axios from 'axios'

export default class CreatePost extends Component{
    constructor(props){
        super(props);
        this.state={
            topic: "",
            description:""
        }
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        }
        
        // To add new employee when user submits the form
        handleSubmit = (event) => {
        event.preventDefault();
        const { topic,description } = this.state;
        axios.post('http://localhost:8070/post/save', {
                    topic:topic,
                    description:description
        })
        .then((response) => {
        console.log(response);
        this.props.history.push('/');
        })
        .catch((error) => {
        console.log(error);
        });
        
    }
    render(){
        return(
            <div className='col-md-8 mt-4 mx-auto'>
                <h1 className='h3 mb-3 font-weight-normal'>Create New Post</h1>
                <form className="needs-validation" noValidate>
                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Topic</lable>
                        <input type="text"
                        name ="topic"
                        className='form-control'
                        placeholder="Enter Topic"
                        value={this.state.topic}
                        onChange={this.handleInputChange}/>

                    </div>

                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Description</lable>
                        <input type="text"
                        name ="description"
                        className='form-control'
                        placeholder="Enter Description"
                        value={this.state.description}
                        onChange={this.handleInputChange}/>

                    </div>
                    <button className='btn btn-success' type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
                        <i className='far fa-check-square'></i>Submit
                    </button>

                </form>
            </div>
        )
    }
}
