import React, { Component } from 'react';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';
 // To use routing functionalities
import { Link } from 'react-router-dom';
 
import ScheduleService from './timeService';
import "../css/notice.css";
 
var divStyle = {
margin: '8% 8%',
};
 
export default class ListTimetable extends Component {
 
constructor(props) {
      super(props);
      this.scheduleService = new ScheduleService();
      this.state = {
      timetables: []
      }
      this.deleteTimetable = this.deleteTimetable.bind(this);
}
 
componentDidMount  () {
    
 this.getTimetableList();
}
 
 // To get all the employees
getTimetableList() {
 axios.get('http://localhost:8070/timetables/')
 .then((response) => {
 console.log(response);
 this.setState({
    timetables: response.data
 });
})
 .catch((error) => {
 console.log(error);
 })
}
 
 // To delete any employee
deleteTimetable(id) {
 this.scheduleService.deleteTimetable(id);
 this.getTimetableList();
}
 
render() {
   
    
const { timetables } = this.state;
return (
<div style={divStyle}>
<Table responsive>
<thead>
   <tr>
      {/* <th>#</th> */}
      <th>Teacher Name</th>
      <th >Subject</th>
      <th>Day</th>
      <th>Time Start</th>
      <th>Time End</th>
      <th></th>
      <th></th>    
   </tr>
</thead>
<tbody>
{
 timetables && timetables.map((schedule, i) => {
 return (
    <tr className="card" key={i} >
    {/* <td>{i}</td> */}
    <td >{schedule.TeacherName}</td>
    <td >{schedule.Subject}</td>
    <td >{schedule.Day}</td>
    <td >{schedule.TimeStart}</td>
    <td>{schedule.TimeEnd}</td>
    <td >
    <Link to={"/editTimetable/"+schedule._id} style={{backgroundColor:"#0099cc",borderStyle:"None"}}className="btn btn-primary" >Edit</Link>
    </td>
    <td>
    <Button style={{backgroundColor:"#0099cc"}}onClick={() => this.deleteTimetable(schedule._id)} bsstyle="danger" >Delete</Button>
    </td>
 </tr>
 )
 })
 }
 </tbody>
 </Table>
 </div>
 );
 } 
 }
