import React , { Component} from 'react';
import axios from 'axios';

export default class ListPosts extends Component{
    constructor(props){
        super(props);

        this.state={
            posts:[]
        };
    }

    componentDidMount(){
        this.retrivePosts();
    }
retrivePosts(){
    axios.get("http://localhost:8070/posts").then(res=>{
        if(res.data.success){
            this.setState({
                posts:res.data.existingPosts
            });

            console.log(this.state.posts)
        }
    })
}

render(){
    return(
        <div>
            <p>All Posts</p>
            <table className='table'>
                <thead>
                    <tr>
                        <th scope='col'>#</th>
                        <th scope='col'>#</th>
                        <th scope='col'>#</th>
                        <th scope='col'>#</th>
                        
                    </tr>
                </thead>
            </table>
        </div>
    )
}

}
