import React,{useState,useEffect}from 'react'
import axios from 'axios'
/*import timetable from '../../../backend/models/timetable'*/


export default function AllTimeTables(){

    const[timetables,setTimetables]=useState([])

const[subjectName,setsubjectName] = useState(0);
 
   

    useEffect(()=>{
        axios.get("http://localhost:8070/timetable/")
        .then(res=>{
            console.log(res)
            setTimetables(res.data)
        })
        .catch(err=>{
            console.log(err)
        })
    },[])

   const updatetable = (id) =>{
    axios.put('http://localhost:8070/update',{id,subjectName})
   }
  
   
    
    return(
        <div className="container">
        <br></br>
            <table border='1'>
                <th align='center'>Subject</th>
                <br></br>
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.subjectName}</ul>))
                }
                <th>Day</th>
               
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.Day}</ul>))
                }
                 <th>Time Start</th>
                
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeStart}</ul>))
                }
                <th>Time End</th>
              
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeEnd}</ul>))
                }
               
               
               </table>
               <div className="container">
      {
       timetables.map((val,key) => {
          return <div key={key}>
            <h1>{val.subjectName}</h1>
            <h1>{val.Day}</h1>
            <input type="text" placeholder='update' onChange={(e) => {
              setNewTable(e.target.value)
            }}/>
            <button className="update-btn"  onClick={() => {updatetable(val._id)}}>Update</button>
          </div>
        })
      }

    </div>      
          
                
            
        </div>
    )
}
