import React, { useState } from 'react';

import axios from 'axios';

function UpdateTimeTable(){
const[subjectName,setsubjectName] = useState(0);
  const[Day,setDay] = useState(0);
  const[TimeStart,setTimeStart] = useState(0);
  const[TimeEnd,setTimeEnd] = useState(0);

  const [timetables,setTimetables]=useState

  const update = (id)=>{
    axios.put('http://localhost:8070/update',{id,subjectName,Day,TimeStart,TimeEnd})
  }

  return(
    <div className="container">
        {
             timetables = timetables.map((val,key)=>{
                return <div key={key}>
                    <h1>{val.subjectName}</h1>
                    <h1>{val.Day}</h1>
                    <input type="text" placeholder='update' onChange={(e) => {
              setsubjectName(e.target.value)
            }}/>
            <button className="update-btn"  onClick={() => {update(val._id)}}>Update</button>
            </div>
            })
        }
    </div>
  )
}
export default UpdateTimeTable;
