import React, { Component } from 'react';
import axios from 'axios';

class UpdateTimeTable extends Component{
  constructor(){
    super();
    this.state={
            subjectName:'',
            Day: '',
            TimeStart : '',
            TimeEnd : ''

    }
  }
  componentDidMount=()=>{
    this.getTimetableById();
  }
  getTimetableById(){
    axios.get('http://localhost:8070/timetable/get/'+this.props.match.params.id)
    .then((response)=>{
      this.setState({
        subjectName:response.data.subjectName,
        Day:response.data.Day,
        TimeStart:response.data.TimeStart,
        TimeEnd:response.data.TimeEnd
      });
    })
    .catch((error) => {
      console.log(error);
     
  })
}
  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
}
handleSubmit=(event)=>{
  event.preventDefault();
  const{
    subjectName,Day,TimeStart,TimeEnd}=this.state;
    axios.post('http://localhost:8070/timetable/update/' + this.props.match.params.id, {
      subjectName:subjectName,
      Day:Day,
      TimeStart:TimeStart,
      TimeEnd:TimeEnd,})
      .then((response) => {
        console.log(response);
        this.props.history.push('/');
      })
      .catch((error) => {
      console.log(error);
      });
      
    }
  render(){
    return(
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>Subject name</label>
          <input type="text" value={this.state.subjectName}
          onChange={this.handleChange}/>

          <label>Day</label>
          <input type="text" value={this.state.Day}
          onChange={this.handleChange}/>

          <label>Time Start</label>
          <input type="text" value={this.state.TimeStart}
          onChange={this.handleChange}/>

          <label>Time End</label>
          <input type="text" value={this.state.TimeEnd}
          onChange={this.handleChange}/>

            <br />
            <input
            type="submit"
            value="submit"
            className="btn btn-primary"
            />


        </form>
      </div>
    );
  }

  }

  export default UpdateTimeTable;
