import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import axios from 'axios';


class AddTimeTable  extends Component {
  constructor() {
    super();
    this.state = {
        subjectName:'',
        Day: '',
        TimeStart : '',
        TimeEnd : ''

    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const data = {
        subjectName:this.state.subjectName,
        Day:this.state.Day,
        TimeStart :this.state.TimeStart,
        TimeEnd : this.state.TimeEnd

    };

    axios
      .post("http://localhost:8070/timetable/add",data)
      .then(res => {
        this.setState({
            subjectName: '',
            Day : '',
            TimeStart : '',
            TimeEnd : '' 
    
        })
       
      })
      .catch(err => {
        console.log("Error in creating!");
      })
  

return(
  <form onSubmit={this.onSubmit}>
    <div>
      <input type='text' placeholder = 'Subject name' name='name' value={this.state.subjectName} onChange={this.onChange}/>
