import React, { Component } from 'react';
import axios from 'axios';
const customStyle = {
  width: '100%',
  margin: '0 ',
  padding: '12px 20px',
  margin: '8px 0',
  boxsizing: 'borderbox'
}
 
export default class AddTimetable extends Component {
constructor(props) {
super(props);
 this.state = {
    TeacherName:'',
    Subject:'',
    Day:'',
    TimeStart:'',
    TimeEnd:''
 }
}
 
 // When value changes of the fields
handleChange = (event) => {
 this.setState({ [event.target.name]: event.target.value });
}
 
 // To add new employee when user submits the form
handleSubmit = (event) => {
 event.preventDefault();
 const { TeacherName,Subject,Day,TimeStart,TimeEnd } = this.state;
 axios.post('http://localhost:8070/timetables/addTimetable', {
   TeacherName:TeacherName,
   Subject:Subject,
   Day:Day,
   TimeStart:TimeStart,
   TimeEnd:TimeEnd
 })
 .then((response) => {
 console.log(response);
 this.props.history.push('/');
 })
 .catch((error) => {
 console.log(error);
 });
}
 
        render() {
        return (
        <div className="container" width='15px'>
        <form style={customStyle} onSubmit={this.handleSubmit}>
        <label >
            Teacher Name
        <input
            name="TeacherName"
            type="text"
            value={this.state.TeacherName}
            onChange={this.handleChange}
            className="form-control"/>
        </label>
        <br />
        
        <label>
            Subject
        <input
            name="Subject"
            type="text"
            value={this.state.Subject}
            onChange={this.handleChange}
            className="form-control"/>
        </label>
        <br />

        <label>
            Day
        <input
            name="Day"
            type="text"
            value={this.state.Day}
            onChange={this.handleChange}
            className="form-control"/>
        </label>
        <br />

        <label>
            Time Start
        <input
            name="TimeStart"
            type="text"
            value={this.state.TimeStart}
            onChange={this.handleChange}
            className="form-control"/>
        </label>
        <br />

        <label>
            Time End
        <input
            name="TimeEnd"
            type="text"
            value={this.state.TimeEnd}
            onChange={this.handleChange}
            className="form-control"/>
        </label>
        <br/>
        <br/>

        <input
        type="submit"
        value="submit"
        className="btn btn-primary"
        />
       
        </form>
        
        </div>
 );
 }
 }
