import React, { Component } from 'react';
import axios from 'axios';



class AddTimeTable extends Component{
    constructor(props){
        super(props);
        this.onChangesubjectName = this.onChangesubjectName.bind(this);
        this.onChangeDay = this.onChangeDay.bind(this);
        this.onChangeTimeStart = this.onChangeTimeStart.bind(this);
        this.onChangeTimeEnd = this.onChangeTimeEnd.bind(this);

        this.state ={
            subjectName:'',
            Day:'',
            TimeStart:'' ,
            TimeEnd:'',
        }

    }
        

    
    

    onChangesubjectName(e){
        this.setState({
            subjectName:e.target.value
        })
    }
    onChangeDay(e){
        this.setState({
            Day:e.target.value
        })
    }
    onChangeTimeStart(e){
        this.setState({
            TimeStart:e.target.value
        })
    }

    onChangeTimeEnd(e){
        this.setState({
            TimeEnd:e.target.value
        })
    }
    onSubmit(e){
        e.preventDefault();

        const timetable = {
            subjectName:this.state.subjectName,
            Day:this.state.Day,
            TimeStart:this.state.TimeStart,
            TimeEnd:this.state.TimeEnd
        }
        console.log(timetable);
        axios.post('http://localhost:8070/timetable/add' , timetable)
      .then(res => console.log(res.data));
     
    }

    render(){
        return(
            <div>
                <h3>Edit Timetable</h3>
                <form onSubmit={this.onSubmit}>
                    <label>Subject Name:</label>
                    <input type ="text" value={this.state.subjectName}
                    onChange={this.onChangesubjectName}/>

                    <label>Day:</label>
                    <input type ="text" value={this.state.Day}
                    onChange={this.onChangeDay}/>

                    <label>Time Start:</label>
                    <input type ="text" value={this.state.TimeStart}
                    onChange={this.onChangeTimeStart}/>

                    <label>Time End:</label>
                    <input type ="text" value={this.state.TimeEnd}
                    onChange={this.onChangeTimeEnd}/>
                </form>

                <div >
          <input type="submit" value="Edit " />
        </div>
            </div>
        )
    }
}
 





export default AddTimeTable;
