import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';
export default class AddNotice extends Component {
  constructor(props) {
    super(props)
    // Setting up functions
    this.onChangeNotice = this.onChangeNotice.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    
    this.onSubmit = this.onSubmit.bind(this);
    // Setting up state
    this.state = {
      Notice:'',
      Date:''
    }
  }
  onChangeNotice(e) {
    this.setState({ Notice: e.target.value })
  }
  onChangeDate(e) {
    this.setState({ Date: e.target.value })
  }
  
  onSubmit(e) {
    e.preventDefault()
    const noticeObject = {
      Notice: this.state.Notice,
      Date: this.state.Date,
      
    };
    axios.post('http://localhost:8070/notices/addNotice', noticeObject)
      .then(res => 
        console.log(res.data));
    this.setState({ Notice:'', Date:''})
  }
  render() {
    return (<div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
       
          <Form.Label>Notice</Form.Label>
          <Form.Control type="text" value={this.state.Notice} onChange={this.onChangeNotice} />
        
       
          <Form.Label>Date</Form.Label>
          <Form.Control type="date" value={this.state.Date} onChange={this.onChangeDate} />
        
       
        <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
          Add Notice
        </Button>
      </Form>
    </div>);
  }
}
