import React from "react";

import { useState } from "react";
import Axios from "axios";


export default function Create() {
  
    const [topic1,setTopic] = useState(null);
    const [desc1,setDescription] = useState(null);
   
  const postData = () => {
    const data = {
        topic:topic1,
        description:desc1,
       
    };
    Axios.post("http://localhost:8070/post/save", data).then((res) => {
      window.location.reload();
    });
  };

  return (
    <div style={{ float: "left" }}>
      <p>
       Topic <input value={topic1} onChange={(e) => setTopic(e.target.value)} />
      </p>
      <p>
        description<input value={desc1} onChange={(e) => setDescription(e.target.value)} />
      </p>
      
      <button onClick={() => postData()}>Submit</button>
    </div>
  );
}
