import axios from 'axios';
import React , { Component} from 'react';
import { useParams } from "react-router-dom";

export default class EditPost extends Component{
   
    constructor(props){
        super(props);
        this.state={
            topic: "",
            description:""
        }
    }

    componentDidMount = () => {
        this.getPostById();
        }
        
        // To get employee based on ID
        getPostById(id) {
        axios.get('http://localhost:8070/employees/editEmployee/' )
        .then((response) => {
        this.setState({
        firstName: response.data.firstName,
        lastName: response.data.lastName,
        email: response.data.email,
        phone: response.data.phone
        });
        })
        .catch((error) => {
        console.log(error);
        })
        }
        
        handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        }
        
        // To update the record on submit
        handleSubmit = (event) => {
        event.preventDefault();
        const { firstName, lastName, email, phone } = this.state;
        axios.post('http://localhost:8070/employees/updateEmployee/'+this.props.match.params.id,  {
        firstName: firstName,
        lastName: lastName,
        email: email,
        phone: phone
        })
        .then((response) => {
        console.log(response);
        this.props.history.push('/');
        })
        .catch((error) => {
        console.log(error);
        });
        
        
    }
    componentDidMount(){


        axios.get(`/post/${id}`).then((res)=>{
            if(res.data.success){
                this.setState({
                    topic:res.data.post.topic,
                    description:res.data.post.description
                });
                console.log(this.state.post);
            }
        })
    }
    render(){
        return(
            <div className='col-md-8 mt-4 mx-auto'>
                <h1 className='h3 mb-3 font-weight-normal'>Create New Post</h1>
                <form className="needs-validation" onSubmit={this.handleSubmit}noValidate>
                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Topic</lable>
                        <input type="text"
                        name ="topic"
                        className='form-control'
                        placeholder="Enter Topic"
                        value={this.state.topic}
                        onChange={this.handleChange}/>

                    </div>

                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Description</lable>
                        <input type="text"
                        name ="description"
                        className='form-control'
                        placeholder="Enter Description"
                        value={this.state.description}
                        onChange={this.handleChange}/>

                    </div>
                    <input
 type="submit"
 value="update"
 className="btn btn-primary"
 />

                </form>
            </div>
        )
    }
}
