import React,{useState,useEffect}from 'react'
import axios from 'axios'

export default function AllTimeTables(){

    const[timetables,setTimetables]=useState([])

    useEffect(()=>{
        axios.get("http://localhost:8070/timetable/")
        .then(res=>{
            console.log(res)
            setTimetables(res.data)
        })
        .catch(err=>{
            console.log(err)
        })
    },[])
    const deletetimetables = (id) => {
        console.log(id);
    
        axios
          .delete(`/delete/${id}`)
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    
        window.location.reload();
      };
  
    return(
        <div className="container">
        <br></br>
            <table border='1'>
                <th>Subject</th>
               
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.subjectName}</ul>))
                }
                <th>Day</th>
               
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.Day}</ul>))
                }
                 <th>Time Start</th>
                
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeStart}</ul>))
                }
                <th>Time End</th>
              
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeEnd}</ul>))
                }
               
               
            </table>
            <button
                    onClick={() => deletetimetables({timetableTimeEnd})}
                    
                  >
                    DELETE
                  </button>
            <a href="/add">Back</a>
        </div>
    )
}
