import React, { Component } from 'react';
 import axios from 'axios';
 
 const customStyle = {
 width: '300px',
 margin: '0 auto'
 }
 
 class AddTimeTable extends Component {
    constructor(props) {
      super(props);
        this.state = {
            subjectName:'',
            Day: '',
            TimeStart : '',
            TimeEnd : ''

 }
 }
 
 // When value changes of the fields
 handleChange = (event) => {
 this.setState({ [event.target.name]: event.target.value });
 }
 
 // To add new employee when user submits the form
 handleSubmit = (event) => {
 event.preventDefault();
    const { subjectName, Day, TimeStart,  TimeEnd } = this.state;
    axios.post('http://localhost:8070/timetable/add', {
      subjectName:subjectName,
      Day:Day,
      TimeStart :TimeStart,
      TimeEnd : TimeEnd

 })
 .then((response) => {
 console.log(response);
 this.props.history.push('/');
 })
 .catch((error) => {
 console.log(error);
 });
 }
 
 render() {
 return (
 <div className="container">
 <form style={customStyle}>
              <label>
              Subject Name
              <input
              name="subjectName"
              type="text"
              value={this.state.subjectName}
              onChange={this.handleChange}
              className="form-control"
              />
              </label>
             <br />

              <label>
              Day
              <input
              name="Day"
              type="text"
              value={this.state.Day}
              onChange={this.handleChange}
              className="form-control"
              />
              </label>
 <br />
              <label>
              Time Start
              <input
              name="TimeStart"
              type="text"
              value={this.state.TimeStart}
              onChange={this.handleChange}
              className="form-control"
              />
              </label>
 <br />
              <label>
              Time End
              <input
              name="TimeEnd"
              type="text"
              value={this.state.TimeEnd}
              onChange={this.handleChange}
              className="form-control"
              />
              </label>
 <br />
 <input
 type="submit"
 value="submit"
 className="btn btn-primary"
 />

 </form>
 
 </div>
 );
 }
 }
 
 export default AddTimeTable;
