import Axios from 'axios';
import React  from 'react';
import { useState } from "react";

const useStyles = makeStyles({
    root: {
      minWidth: 300,
    },
    bullet: {
      display: "inline-block",
      margin: "0 2px",
      transform: "scale(0.8)",
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
  });


export default function EditPost ({topic,description,id}){
    
    const [topic1,setTopic] = useState(topic);
    const [desc1,setDescription] = useState(description);
    const [update, setUpdate] = useState(false);

    const deleteData = () => {
        Axios.post("http://localhost:8070/post/delete", { _id: id }).then((res) =>
          window.location.reload()
        );
}
const updateData = () => {
    const data = {
       topic:topic1,
       description:desc1,
      _id: id,
    };
    Axios.post("http://localhost:8070/post/update", data).then((res) => {
      window.location.reload();
    });
  };
  return (
    <div  style={{ float: "left" }}>
      <p>
       Topic :
        {update ? (
          <input value={topic1} onChange={(e) => setTopic(e.target.value)} />
        ) : (
          topic1
        )}
      </p>
      <p>
       description :
        {update ? (
          <input value={desc1} onChange={(e) => setDescription(e.target.value)} />
        ) : (
          desc1
        )}
      </p>
      
      
      {update ? <button onClick={() => updateData()}>Update</button> : null}
      <button onClick={() => setUpdate(true)}>Edit</button>
      <button onClick={() => deleteData()}>Delete</button>
    </div>
  );

}
