import React,{useState,useEffect}from 'react'
import axios from 'axios'
import timetable from '../../../backend/models/timetable'


export default function AllTimeTables(){

    const[timetables,setTimetables]=useState([])

    useEffect(()=>{
        axios.get("http://localhost:8070/timetable/")
        .then(res=>{
            console.log(res)
            setTimetables(res.data)
        })
        .catch(err=>{
            console.log(err)
        })
    },[])

    const arr = timetable.map((timetable,index)=>{
        return(
            <tr>
                <td>{timetable.subjectName}</td>
                <td>{timetable.Day}</td>
                <td>{timetable.TimeStart}</td>
                <td>{timetable.T}</td>
            </tr>
        )
    })
  
    const deleteTimetable=(id,e)=>{
        e.preventDefault();
        axios.delete(`http://localhost:8070/timetable/delete/:id}`)
        .then(res=>{
            console.log(res) })
            .catch(err=>{
                console.log(err)
            })
    }
    
    return(
        <div className="container">
        <br></br>
            <table border='1'>
                <th align='center'>Subject</th>
                <br></br>
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.subjectName}</ul>))
                }
                <th>Day</th>
               
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.Day}</ul>))
                }
                 <th>Time Start</th>
                
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeStart}</ul>))
                }
                <th>Time End</th>
              
              
                {
                    timetables.map(timetable=>(<ul key={timetable.id}>{timetable.TimeEnd}</ul>))
                }
               
               
               </table>
           <button onClick={(e)=>deleteTimetable(timetable.id,e)}>Delete</button>
                
            <a href="/add">Back</a>
        </div>
    )
}
