import React, { useState } from 'react';

import axios from 'axios';

function UpdateTimeTable(){
const[subjectName,setsubjectName] = useState(0);
  const[Day,setDay] = useState(0);
  const[TimeStart,setTimeStart] = useState(0);
  const[TimeEnd,setTimeEnd] = useState(0);

  const update = (id)=>{
    axios.put('http://localhost:8070/update',{id,subjectName,Day,TimeStart,TimeEnd})
  }

  return(
    <div className='container'>
        <label>Subject</label>
        <input type="text" onChange={(e)=>{setsubjectName(e.target.value)}}/>
        <label>Day</label>
        <input type="text" onChange={(e)=>{setDay(e.target.value)}}/>
        <label>TimeStart</label>
        <input type="text" onChange={(e)=>{setTimeStart(e.target.value)}}/>
        <label>TimeEnd</label>
        <input type="text" onChange={(e)=>{setTimeEnd(e.target.value)}}/>

        <button onClick={update}>update </button>

    </div>
  )
   
  } 

export default UpdateTimeTable;
