import Axios from 'axios';
import React  from 'react';
import { useState } from "react";


export default function EditPost ({topic,description}){
    const [topic1,setTopic] = useState(topic);
    const [desc1,setDescription] = useState(description);
    const [update, setUpdate] = useState(false);

    const deleteData = () => {
        Axios.post("http://localhost:8070/post/delete", { _id: id }).then((res) =>
          window.location.reload()
        );
}
const updateData = () => {
    const data = {
       topic:topic1,
       description:desc1,
      _id: id,
    };
    Axios.post("http://localhost:8070/update", data).then((res) => {
      window.location.reload();
    });
  };
  return (
    <Card className={classes.root} style={{ float: "left" }}>
      <p>
        Name :
        {update ? (
          <input value={name1} onChange={(e) => setName(e.target.value)} />
        ) : (
          name1
        )}
      </p>
      <p>
        Age :
        {update ? (
          <input value={age1} onChange={(e) => setAge(e.target.value)} />
        ) : (
          age1
        )}
      </p>
      <p>
        City :
        {update ? (
          <input value={city1} onChange={(e) => setCity(e.target.value)} />
        ) : (
          city1
        )}
      </p>
      <p>
        Country :
        {update ? (
          <input
            value={country1}
            onChange={(e) => setCountry(e.target.value)}
          />
        ) : (
          country1
        )}
      </p>
      {update ? <button onClick={() => updateData()}>Update</button> : null}
      <button onClick={() => setUpdate(true)}>Edit</button>
      <button onClick={() => deleteData()}>Delete</button>
    </Card>
  );

}
