import React, { Component } from 'react';
 import axios from 'axios';
 import { Table, Button } from 'react-bootstrap';
 // To use routing functionalities
 import { Link,NavLink} from 'react-router-dom';
 
 import EmployeeService from './Services';
 
 var divStyle = {
 margin: '8% 8%',
 };
 
 export default class ListEmployee extends Component {
 
 constructor(props) {
 super(props);
 this.employeeService = new EmployeeService();
 this.state = {
 employees: []
 }
 this.deleteEmployee = this.deleteEmployee.bind(this);
 }
 
 componentDidMount  () {
    
 this.getEmployeeList();
 }
 
 // To get all the employees
 getEmployeeList() {
 axios.get('http://localhost:8070/employees/')
 .then((response) => {
 console.log(response);
 this.setState({
 employees: response.data
 });
 })
 .catch((error) => {
 console.log(error);
 })
 }
 
 // To delete any employee
 deleteEmployee(empid) {
 this.employeeService.deleteEmployee(empid);
 this.getEmployeeList();
 }
 
 render() {
 const { employees } = this.state;
 return (
 <div style={divStyle}>
 <Table responsive>
 <thead>
    <tr>
        <th>#</th>
        <th>User ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th></th>
            <th></th>
        </tr>
    </thead>
    <tbody>
 {
 employees && employees.map((employee, i) => {
 return (
    <tr key={i}>
    <td><NavLink to="//"></NavLink>{i}</td>
    <td>{employee.UserID}</td>
    <td>{employee.firstName}</td>
    <td>{employee.lastName}</td>
    <td>{employee.email}</td>
    <td>{employee.phone}</td>
    <td>
    <Link to={`/editEmployee/${employee._id}`} className="btn btn-primary">Edit</Link>
    </td>
    <td>
    <Button onClick={() => this.deleteEmployee(employee._id)} bsstyle="danger" >Delete</Button>
    </td>
 </tr>
 )
 })
 }
 </tbody>
 </Table>
 </div>
 );
 } 
 }
