import axios from 'axios';
import React , { Component} from 'react';

export default class EditPost extends Component{
    constructor(props){
        super(props);
        this.state={
            topic: "",
            description:""
        }
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        }
        
        // To add new employee when user submits the form
        handleSubmit = (event) => {
        event.preventDefault();
        const id = this.props.match.params.id;
        const { topic,description } = this.state;
        axios.put('http://localhost:8070/post/update/${', {
                    topic:topic,
                    description:description
        })
        .then((response) => {
        console.log(response);
        this.props.history.push('/');
        })
        .catch((error) => {
        console.log(error);
        });
        
    }
    componentDidMount(){
        const id = this.props.match.params.id;

        axios.get(`/post/${id}`).then((res)=>{
            if(res.data.success){
                this.setState({
                    post:res.data.post
                });
                console.log(this.state.post);
            }
        })
    }
    render(){
        return(
            <div className='col-md-8 mt-4 mx-auto'>
                <h1 className='h3 mb-3 font-weight-normal'>Create New Post</h1>
                <form className="needs-validation" onSubmit={this.handleSubmit}noValidate>
                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Topic</lable>
                        <input type="text"
                        name ="topic"
                        className='form-control'
                        placeholder="Enter Topic"
                        value={this.state.topic}
                        onChange={this.handleChange}/>

                    </div>

                    <div className="form-group" style={{marginBottom:'15px'}}>
                        <lable style={{marginBottom:'5px'}}>Description</lable>
                        <input type="text"
                        name ="description"
                        className='form-control'
                        placeholder="Enter Description"
                        value={this.state.description}
                        onChange={this.handleChange}/>

                    </div>
                    <input
 type="submit"
 value="submit"
 className="btn btn-primary"
 />

                </form>
            </div>
        )
    }
}
